"use strict";

module.exports = function (content, map, meta) {
  // 只处理 require(变量 + ’path‘) 和 require(变量)的情况
  var code = content.replace(/require\((.*)\)/g, function (s, m) {
    /*eslint-disable*/
    if (!/^\s*['"].*['"]\s*$/g.test(m)) {
      return '$require(' + m + ')';
    }

    return s;
  });
  this.callback(null, code, map, meta);
  return;
};